import React, { Component } from 'react';
import PageTemplate from './PageTemplate';
import TodoInput from './TodoInput/TodoInput';
import TodoList from './TodoList/TodoList';

class App extends Component {
    state = {
        input: ''
    }

    handleChange = (e) => {
        const { value } = e.target;
        this.setState({
            input: value
        })
    }

    render() {
        return (
            <PageTemplate>
                <TodoInput />
                <TodoList />
            </PageTemplate>
        );
    }
}

export default App;
